INSERT INTO `hoidap` (`idHoiDap`, `idLoaiKH`, `idUser`, `TieuDe`, `NoiDung`, `NgayHoi`, `LuotXem`) VALUES
(1, 2, 1, 'Sự giống nhau và khác nhau giữa $_POST, $_GET, $_SERVER, $_REQUEST, $_FILE ?', '<ol>\r\n	<li>Sự giống nhau v&agrave; kh&aacute;c nhau giữa $_POST, $_GET, $_SERVER, $_REQUEST, $_FILE</li>\r\n</ol>\r\n\r\n<p>&nbsp;</p>\r\n', '2017-06-06', 2),
(2, 2, 1, 'MVC là gì ? MVC có gì hay ?', '<p>M&ocirc; hình MVC là gì ?&nbsp;</p>\r\n\r\n<p>Các bạn có th&ecirc;̉ giải thích cho t&ocirc;i hi&ecirc;̉u rõ được kh&ocirc;ng và nó có gì hay nè ?</p>\r\n', '2017-06-06', 1),
(3, 2, 1, 'Tự học lập trình và việc làm của một sinh viên ngành Y', '<p>Xin ch&agrave;o admin,</p>\r\n\r\n<p><strong>M&igrave;nh t&ecirc;n l&agrave;</strong>: Th&agrave;nh</p>\r\n\r\n<p><strong>C&ocirc;ng việc&nbsp;hiện tại l&agrave;</strong>: sinh vi&ecirc;n</p>\r\n\r\n<p><strong>M&igrave;nh cần tư vấn nội dung như sau</strong>: em l&agrave; sinh vi&ecirc;n ng&agrave;nh y nhưng lại muốn học lập tr&igrave;nh. H&egrave; vừa rồi em c&oacute; tự học phần&nbsp;frontend nhưng chỉ mới ở độ biết qua loa. Em năm nay l&agrave; năm 3 rồi, cũng học đến tầm b&agrave;i 20 PHP cơ bản ở đ&acirc;y. Em đều thực h&agrave;nh lại c&aacute;c v&iacute; dụ của freetuts v&agrave; tham khảo th&ecirc;m 1 v&agrave;i web nữa. Nhưng thời gian em d&agrave;nh ra chỉ 1-2h.</p>\r\n\r\n<p>Mong admin cho em lời khuy&ecirc;n c&oacute; n&ecirc;n tiếp tục con đường n&agrave;y kh&ocirc;ng. Em mới tham gia v&agrave;o freelancer.com nhưng mới kiếm được v&agrave;i dự &aacute;n về fontend v&agrave; lặt vặt kh&aacute;c.</p>\r\n\r\n<p>Xin cảm ơn!!!</p>\r\n', '2017-06-10', 54);

