INSERT INTO `tracnghiemchitiet` (`idTNChiTiet`, `idTracNghiem`, `idLevel`, `CauHoi`, `DapAn`) VALUES
(181, 50, 1, '<p><strong>PHP tượng trưng cho c&aacute;i g&igrave;:</strong></p>\r\n', 3),
(183, 50, 1, '<p><strong>&nbsp;Đoạn m&atilde; sau , in ra gi&aacute; trị n&agrave;o sau đ&acirc;y</strong></p>\r\n\r\n<p><strong>&lt;?php</strong></p>\r\n\r\n<p><strong>&nbsp; &nbsp; &nbsp; $a=1;&nbsp;</strong></p>\r\n\r\n<p><strong>function test(){</strong></p>\r\n\r\n<p><strong>&nbsp; &nbsp; &nbsp; &nbsp;echo &quot;a=$a&quot;;</strong></p>\r\n\r\n<p><strong>&nbsp; }</strong></p>\r\n\r\n<p><strong>&nbsp;test();</strong></p>\r\n\r\n<p><strong>?&gt;</strong></p>\r\n', 4),
(184, 50, 1, '<p><strong>Đoạn m&atilde; sau , in ra gi&aacute; trị n&agrave;o sau đ&acirc;</strong></p>\r\n\r\n<p><strong>FUNCTION TEST(){</strong></p>\r\n\r\n<p><strong>&nbsp; &nbsp; &nbsp; echo &#39;HELLO WORLD!&#39;;&nbsp;</strong></p>\r\n\r\n<p><strong>} test();</strong></p>\r\n', 1),
(185, 50, 1, '<p><strong>&nbsp;Đoạn m&atilde;&nbsp;n&agrave;o sau đ&acirc;y được sử dụng để ch&uacute; th&iacute;ch&nbsp;PHP</strong></p>\r\n', 4),
(186, 50, 1, '<p><strong>K&yacute; hiệu n&agrave;o được d&ugrave;ng khi sử dụng biến trong PHP</strong></p>\r\n', 2),
(187, 50, 1, '<p><strong>Đoạn m&atilde; sau , in ra gi&aacute; trị n&agrave;o sau đ&acirc;y</strong></p>\r\n\r\n<div style="background:#eeeeee;border:1px solid #cccccc;padding:5px 10px;"><strong>$check=&#39;false&#39;; if($check){ echo &#39;true&#39;; } else{echo &#39;false&#39;;}</strong></div>\r\n', 2),
(188, 50, 1, '<p><strong>&nbsp;H&agrave;m&nbsp;n&agrave;o sau đ&acirc;y d&ugrave;ng để khai b&aacute;o hằng số</strong></p>\r\n', 3),
(189, 50, 1, '<p><strong>&nbsp;Đoạn m&atilde; sau , in ra gi&aacute; trị n&agrave;o sau đ&acirc;y</strong></p>\r\n\r\n<p><strong>$var = &#39;0&#39;;</strong></p>\r\n\r\n<p><strong>if ($var) { echo &#39;true&#39;; } else { echo &#39;false&#39;; }</strong></p>\r\n', 1),
(220, 54, 1, '<p><strong>Đ&aacute;p &aacute;n n&agrave;o sau đ&acirc;y kh&ocirc;ng được x&aacute;c định trước bởi PHP ( &nbsp;Magic constants)</strong></p>\r\n', 3),
(221, 54, 1, '<p><strong>Đoạn m&atilde; sau , in ra gi&aacute; trị n&agrave;o sau đ&acirc;y</strong></p>\r\n\r\n<blockquote>\r\n<p>$a = array(); if ($a == null) { echo &#39;true&#39;; } else { echo &#39;false&#39;; }</p>\r\n</blockquote>\r\n', 1),
(222, 54, 1, '<p><strong>Đoạn m&atilde; sau , in ra gi&aacute; trị n&agrave;o sau đ&acirc;y</strong></p>\r\n\r\n<blockquote>\r\n<p>if (null === false) { echo &#39;true&#39;; } else { echo &#39;false&#39;; }<strong>​​​​​​​</strong></p>\r\n</blockquote>\r\n', 2),
(223, 54, 1, '<p><strong>Đoạn m&atilde; sau , in ra gi&aacute; trị n&agrave;o sau đ&acirc;y</strong></p>\r\n\r\n<blockquote>\r\n<p>$RESULT = 11 + 11 + 0x11;</p>\r\n\r\n<p>echo &quot;$RESULT&quot;;</p>\r\n</blockquote>\r\n', 3),
(224, 54, 1, '<p><strong>Đ&acirc;u kh&ocirc;ng phải l&agrave; ph&eacute;p to&aacute;n được d&ugrave;ng so s&aacute;nh trong PHP</strong></p>\r\n', 4),
(225, 54, 1, '<p><strong>Đoạn m&atilde; sau , in ra gi&aacute; trị n&agrave;o sau đ&acirc;y</strong></p>\r\n\r\n<blockquote>\r\n<p>$var = &#39;a&#39;;</p>\r\n\r\n<p>$VAR = &#39;b&#39;;</p>\r\n\r\n<p>echo &quot;$var$VAR&quot;;</p>\r\n</blockquote>\r\n', 1),
(226, 54, 1, '<p><strong>Đoạn m&atilde; sau , in ra gi&aacute; trị n&agrave;o sau đ&acirc;y</strong></p>\r\n\r\n<blockquote>\r\n<p>$a = array(</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; null =&gt; &#39;a&#39;, true =&gt; &#39;b&#39;, false =&gt; &#39;c&#39;, 0 =&gt; &#39;d&#39;, 1 =&gt; &#39;e&#39;, &#39;&#39; =&gt; &#39;f&#39;</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; );</p>\r\n\r\n<p>echo count($a);</p>\r\n</blockquote>\r\n', 2),
(227, 54, 1, '<p><strong>Đoạn m&atilde; sau , in ra gi&aacute; trị n&agrave;o sau đ&acirc;y</strong></p>\r\n\r\n<blockquote>\r\n<p>$a = 1; { $a = 2; }</p>\r\n\r\n<p>echo $a;</p>\r\n</blockquote>\r\n', 2),
(228, 54, 1, '<p><strong>Đoạn m&atilde; sau , in ra gi&aacute; trị n&agrave;o sau đ&acirc;y</strong></p>\r\n\r\n<blockquote>\r\n<p>$str = &#39;a&#39;;</p>\r\n\r\n<p>echo $str;</p>\r\n</blockquote>\r\n', 3),
(229, 54, 1, '<p><strong>Nhận định n&agrave;o đ&uacute;ng khi n&oacute;i về đoạn m&atilde; sau</strong></p>\r\n\r\n<blockquote>\r\n<p>class A {}</p>\r\n\r\n<p>class B {}</p>\r\n\r\n<p>class C extends A, B {}</p>\r\n</blockquote>\r\n', 3),
(250, 50, 1, '<p><strong>Gi&aacute; trị của tham số sau</strong></p>\r\n\r\n<p><strong>$var = 1 / 2;</strong></p>\r\n', 3),
(276, 63, 1, '<p><strong>Composer&nbsp;là gì ?</strong></p>\n', 1),
(277, 63, 1, '<p><strong>C&acirc;u l&ecirc;̣nh cài đặt composer trong laravel :</strong></p>\n', 1),
(278, 63, 1, '<p><strong>C&acirc;u l&ecirc;̣nh nào c&acirc;̣p nh&acirc;̣t composer&nbsp;</strong></p>\n', 1);

